import 'package:flutter/material.dart';
import 'package:carousel_slider/carousel_slider.dart';

class SearchBarApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Search Bar',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: SearchScreen(),
    );
  }
}

class SearchScreen extends StatefulWidget {
  @override
  _SearchScreenState createState() => _SearchScreenState();
}

class _SearchScreenState extends State<SearchScreen> {
  String _searchText = "";
  List<String> _data = [];
  List<String> _filteredData = [];
  List<String> _imageUrls = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQCd4QED50IAiKpDAEV-WdGvmYe_awTrXhXnA',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQkrxPEVCr19Vzc7zvP870_7f_V3G9Tl9CuWA',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdQxlNsbXtm-yollyo04etyQiCIYWmIOM_bw',
  ];
  @override
  void initState() {
    super.initState();
    _filteredData = _data;
  }

  void _filterSearchResults(String query) {
    List<String> filteredList = [];
    filteredList.addAll(_data);
    if (query.isNotEmpty) {
      filteredList.retainWhere(
          (item) => item.toLowerCase().contains(query.toLowerCase()));
    }

    setState(() {
      _searchText = query;
      _filteredData = filteredList;
    });
  }

  Widget _buildCarousel() {
    return CarouselSlider(
      options: CarouselOptions(
        autoPlay: true,
        aspectRatio: 16 / 9,
        autoPlayCurve: Curves.fastOutSlowIn,
        autoPlayInterval: const Duration(seconds: 5),
        autoPlayAnimationDuration: Duration(milliseconds: 800),
        enableInfiniteScroll: true,
        pauseAutoPlayOnTouch: true,
        scrollDirection: Axis.horizontal,
      ),
      items: _imageUrls.map((imageUrl) {
        return Builder(
          builder: (BuildContext context) {
            return Container(
              width: MediaQuery.of(context).size.width,
              margin: const EdgeInsets.symmetric(horizontal: 5.0),
              child: Image.network(
                imageUrl,
                fit: BoxFit.fitWidth,
              ),
            );
          },
        );
      }).toList(),
    );
  }

  Widget _buildLogo() {
    return SizedBox(
      width: 150,
      height: 150,
      child: Transform.scale(
        scale: 1.0,
        child: Image.asset('assets/images/inkLogo.png'),
      ),
    );
  }

  Widget _buildSearch() {
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: TextField(
        onChanged: (value) {
          _filterSearchResults(value);
        },
        decoration: const InputDecoration(
          labelText: 'Search',
          prefixIcon: Icon(Icons.search),
          border: OutlineInputBorder(
            borderRadius: BorderRadius.all(Radius.circular(25.0)),
          ),
        ),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Column(
        children: <Widget>[
          Container(
            height: MediaQuery.of(context).size.height -
                395, // Altura dispon√≠vel para o carrossel
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              crossAxisAlignment: CrossAxisAlignment.center,
              children: <Widget>[
                _buildLogo(),
                _buildSearch(),
              ],
            ),
          ),
          _buildCarousel(),
        ],
      ),
    );
  }
}
